(function($){
    /***************** Timer ******************/
    function timer() {
        var d = 0,
                h = 0,
                m = 0,
                s = 0,
                date=null;
        var $timer = $('[data-timer="true"]'),
            date = $timer.data('date');
        console.log($timer);
        if ((new Date(date)).getTime() >= (new Date()).getTime()) {
            s = (new Date(date)).getTime() - (new Date()).getTime();
            s = parseInt(s / 1000);
            d = parseInt(s / (24*3600));
            s -= d * (24*3600);
            h = parseInt(s / 3600);
            s -= h * 3600;
            m = parseInt(s / 60);
            s -= m * 60;
            //$timer.update();
            console.log(d + ':' + h + ':' + m + ':' + s);
        }
        $timer.find('#d').text(d);
        $timer.find('#h').text(h);
        $timer.find('#m').text(m);
        $timer.find('#s').text(s);
        if ((new Date(date)).getTime() >= (new Date()).getTime()) setTimeout(timer, 1000);
    }

    var formInit = (function(){
        var $courseForm = $('[data-form="course"]'),
            $step1 = $courseForm.find('.step-form[data-step="1"]');

        function switchCourse(){
            $('body').on('click', '[data-method="switch_course"]', function () {
                var self = this,
                    id=$(self).val();
                $.get($(self).data('route'),{id:id})
                    .done(function(data){
                        $step1.find('#price').text(data.price);
                        $step1.find('input[name="amount"]').val(data.price);
                        $step1.find('[data-timer="true"]').data('date',data.date_of_increase);
                        $step1.find('[data-timer="true"]').attr('data-date',data.date_of_increase);
                    })
                    .fail(function(data){
                        console.log('something went wrong')
                    })
            })
        }
        function submitHandler() {
            $courseForm.on('click','input[data-next-step]',function(){
                var $self = this,
                    $form = $($self).parents('form'),
                    $currentStep = $($self).parents('.step-form').data('step'),
                    $nextStep = $($self).data('next-step'),
                    url = $form.attr('action');
                //$($self).attr('disabled','disabled');
                $.post(
                    url,
                    $form.serializeArray()
                ).done(function(data) {
                    $courseForm.find('button[data-next-step="'+$nextStep+'"]').removeAttr('disabled');
                    $courseForm.find('.step-form[data-step="'+$currentStep+'"]').fadeIn(250,function(){
                        $('.form-step-one[data-step="'+$currentStep+'"]').removeClass('active');
                        var result = data.result;
                        console.log(result);
                        if($currentStep == 1) {
                            step1(result);
                        }
                        //$courseForm.find('.step-form[data-step="'+$nextStep+'"]').slideDown(250);
                        $courseForm.find('.step-form[data-step="'+$nextStep+'"]').removeClass('hide');
                        $courseForm.find('.form-step-one[data-step="'+$nextStep+'"]').addClass('active');
                    });
                    $('label.error').hide();
                    switch ($('.form-step-one.active').attr('data-step')) {
                        case '1':
                            break;
                        case '2':
                            $('.form-step-one[data-step="1"]').addClass('active');
                            $('.progress-bar-wrapper').addClass('fifty-percent-complete');
                            break;
                        case '3':
                            $('.form-step-one[data-step="1"], .form-step-one[data-step="2"]').addClass('active');
                            $('.progress-bar-wrapper').addClass('completed');
                            break;
                    }
                }).fail(function(data) {

                    $errors = data.responseJSON;
                     $.each($errors, function(i,val){
                         $errors = val;
                     });
                     swal('Некоторые поля заполнены неверно', $errors, 'warning');

                    $courseForm.find('button[data-next-step="'+$nextStep+'"]').removeAttr('disabled');
                })
                return false;
            });

        }
        
        function step1(result) {
            if(result.html ) {
                $courseForm.find('.step-form[data-step="1"]').html(result.html)
            }
        }
        return {
            init:function() {
                submitHandler()
            },
            switch_course:function() {
                switchCourse()
            }
        }
    }());// end of Course Registration name space
    if($('[data-timer="true"]').length>0) {
        timer();
        formInit.switch_course()
    }
    if($('[data-form="course"]').length==1) {
        formInit.init()
    }
})(jQuery); // end of jQuery name space
