jQuery(document).ready(function($){
	// browser window scroll (in pixels) after which the "back to top" link is shown
	var offset = 300,
		//browser window scroll (in pixels) after which the "back to top" link opacity is reduced
		offset_opacity = 1200,
		//duration of the top scrolling animation (in ms)
		scroll_top_duration = 700,
		//grab the "back to top" link
		$back_to_top = $('.cd-top');

	//hide or show the "back to top" link
	$(window).scroll(function(){
		( $(this).scrollTop() > offset ) ? $back_to_top.addClass('cd-is-visible') : $back_to_top.removeClass('cd-is-visible cd-fade-out');
		if( $(this).scrollTop() > offset_opacity ) { 
			$back_to_top.addClass('cd-fade-out');
		}
	});

	//smooth scroll to top
	$back_to_top.on('click', function(event){
		event.preventDefault();
		$('body,html').animate({
			scrollTop: 0
		 	}, scroll_top_duration
		);
	});

});
(function($){
    $(function(){

        $('.modal-trigger').leanModal();
        $('.flexslider').flexslider({
            animation: "slide"
        });
        $('.button-collapse').sideNav();


        if ($(window).width() > 600) {
            var s = skrollr.init({
                forceHeight: false,
                smoothScrolling: true,
            });
        }

        /***************** Header Scroll ******************/
        $(function() {
            $(window).scroll(function() {
                var scroll = $(window).scrollTop();

                if (scroll >= 20) {
                    $('.main-nav').addClass('fixed-bg');
                } else {
                    $('.main-nav').removeClass('fixed-bg');
                }
            });
        });

    }); // end of document ready

    $('.tab div').bind("click", function () {
        var a = $(this).find('a').attr('href');
        var t = 'a[href=' + a + ']';
        if ($(a).css('display') == 'block') {
            $(a).slideDown();
            $('.tab a').css('color', '#000');
            $(this).removeClass('active');
            $(this).parent().removeClass('active');
        }
        else {
            $('.lesson').slideUp();
            $('.tab div').removeClass('active');
            $('.tab li').removeClass('active');
            $('.tab a').css('color', '#000');
            $(this).addClass('active');
            $(this).parent().addClass('active');
            $(a).slideDown();
            $(t).css('color', '#7c9a51');
        }
        return false;
    });

    /*старые табы, можно удалить*/
    /*$('ul[date-tabsP] > li').click(function () {
        var tabsP = $(this).parent().attr('date-tabsP');
        var tabs = $(this).attr('date-tabsC');
        $('[date-tabsP="' + tabsP + '"][data-tabsC]').hide();
        $('.' + tabs).show();
        $(this).parent().children().removeClass('active');
        $(this).addClass('active');
        return false;
    });*/

    var coursesTabs = $('.tabsA .tab-link');
    var tabContent = $('.courses-1');
    coursesTabs.click(function (e) {
        $this = e.target;
        coursesTabs.removeClass('current');
        tabContent.removeClass('current');
        $(this).addClass('current');
        $("#"+$(this).attr('data-tab')).addClass('current');
        $('.slick-next').trigger('click');/*убирает баг с инициализацией слайдера при переключении табов*/
    });


    /*$(window).resize(function () {
        SandvichMenu;
        /!*console.log('resizing')*!/
    });*/



})(jQuery); // end of jQuery name space
